import React, {useState} from 'react';
import axios from 'axios';

const useFlip = () => {
    const [flip, setFlip] = useState(true);

    const toggleFlip = () => {
        setFlip(!flip);
    }

    return [flip, toggleFlip]
}

export {useFlip};



const useAxios = (keyInLS, baseUrl) => {
    const [data, setData] = useState(keyInLS)

    const addData = async (formatter = data => data, restOfUrl = '') => {
        const res = await axios.get(`${baseUrl}${restOfUrl}`)
        setData(data => [...data, formatter(res.data)]);
    };
    return [data, addData];
}

export {useAxios};
